// src/components/Signup.js
import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Select, MenuItem, InputLabel, FormControl, Box } from '@mui/material';

const Signup = () => {
    const [name, setName] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [role, setRole] = useState('student');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignup = () => {
        const signupData = { 
            name, 
            company_name: role === 'mentor' ? companyName : null, 
            role, 
            password 
        };

        axios.post('http://localhost:5000/api/auth/signup', signupData)
            .then(response => {
                alert('Signup successful!');
                navigate('/login');
            })
            .catch(err => {
                console.error(err);
                alert('Error during signup. Please try again.');
            });
    };

    return (
        <Container maxWidth="sm">
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: 2,
                    mt: 5,
                    p: 7,
                    boxShadow: 3,
                    borderRadius: 2,
                    background: '#1212125a',
                    backdropFilter: 'blur(10px)'
                }}
            >
                <Typography variant="h4" component="h1" gutterBottom>
                    Signup
                </Typography>
                <TextField
                    label="Name"
                    variant="outlined"
                    fullWidth
                    value={name}
                    onChange={e => setName(e.target.value)}
                />
                {role === 'mentor' && (
                    <TextField
                        label="Company Name"
                        variant="outlined"
                        fullWidth
                        value={companyName}
                        onChange={e => setCompanyName(e.target.value)}
                    />
                )}
                <FormControl fullWidth>
                    <InputLabel id="role-select-label">Role</InputLabel>
                    <Select
                        labelId="role-select-label"
                        value={role}
                        onChange={e => setRole(e.target.value)}
                        label="Role"
                    >
                        <MenuItem value="student">Student</MenuItem>
                        <MenuItem value="mentor">Mentor</MenuItem>
                    </Select>
                </FormControl>
                <TextField
                    label="Password"
                    type="password"
                    variant="outlined"
                    fullWidth
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                />
                <Button 
                    variant="contained" 
                    color="primary" 
                    fullWidth 
                    onClick={handleSignup}
                >
                    Signup
                </Button>
                <Typography variant="body2">
                    Already have an account? <Link to="/login">Login</Link>
                </Typography>
            </Box>
        </Container>
    );
};

export default Signup;
