// src/components/Scheduler.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Typography, Select, MenuItem, InputLabel, FormControl, Box } from '@mui/material';

const Scheduler = () => {
    const [mentors, setMentors] = useState([]);
    const [studentName, setStudentName] = useState('');
    const [areaOfInterest, setAreaOfInterest] = useState('');
    const [duration, setDuration] = useState(30);
    const [sessionTime, setSessionTime] = useState('');
    const [selectedMentor, setSelectedMentor] = useState('');
    const [filteredMentors, setFilteredMentors] = useState([]);
    const [price, setPrice] = useState(0);

    useEffect(() => {
        const dummyMentors = [
            { id: '1', name: 'John Doe', area_of_interest: 'FMCG Sales' },
            { id: '2', name: 'Jane Smith', area_of_interest: 'Equity Research' },
            { id: '3', name: 'Alice Johnson', area_of_interest: 'Digital Marketing' },
            { id: '4', name: 'Bob Brown', area_of_interest: 'FMCG Sales' },
            { id: '5', name: 'Charlie Davis', area_of_interest: 'Digital Marketing' }
        ];
        setMentors(dummyMentors);
    }, []);

    useEffect(() => {
        const filtered = mentors.filter(mentor => mentor.area_of_interest === areaOfInterest);
        setFilteredMentors(filtered);
    }, [areaOfInterest, mentors]);

    useEffect(() => {
        // Update the price based on the selected duration
        switch (duration) {
            case 30:
                setPrice(50); // Price for 30 mins
                break;
            case 45:
                setPrice(75); // Price for 45 mins
                break;
            case 60:
                setPrice(100); // Price for 60 mins
                break;
            default:
                setPrice(0);
                break;
        }
    }, [duration]);

    const handleSchedule = () => {
        if (!studentName || !areaOfInterest || !selectedMentor || !sessionTime) {
            alert('Please fill in all fields. 📝');
            return;
        }

        const studentData = { name: studentName, area_of_interest: areaOfInterest };

        axios.post('http://localhost:5000/api/students', studentData)
            .then(response => {
                const sessionData = {
                    student_id: response.data.id,
                    mentor_id: selectedMentor,
                    duration,
                    session_time: sessionTime
                };

                return axios.post('http://localhost:5000/api/sessions', sessionData);
            })
            .then(() => {
                alert('Session scheduled successfully! 🎉');
                setStudentName('');
                setAreaOfInterest('');
                setDuration(30);
                setSessionTime('');
                setSelectedMentor('');
                setPrice(0);
            })
            .catch(err => {
                console.error('Error scheduling session:', err);
                alert('Error scheduling session. Please check your input and try again. ❌');
            });
    };

    return (
        <Container maxWidth="sm">
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: 2,
                    mt: 5,
                    p: 7,
                    boxShadow: 3,
                    borderRadius: 6,
                    background: '#4454835a',
                    backdropFilter: 'blur(10px)'
                }}
            >
                <Typography variant="h4" component="h1" gutterBottom>
                    Schedule a Session 🗓️
                </Typography>
                <TextField
                    label="Student Name"
                    variant="outlined"
                    fullWidth
                    value={studentName}
                    onChange={e => setStudentName(e.target.value)}
                />
                <FormControl fullWidth>
                    <InputLabel id="area-of-interest-label">Area of Interest 🎯</InputLabel>
                    <Select
                        labelId="area-of-interest-label"
                        value={areaOfInterest}
                        onChange={e => setAreaOfInterest(e.target.value)}
                        label="Area of Interest"
                    >
                        <MenuItem value="FMCG Sales">FMCG Sales 📦</MenuItem>
                        <MenuItem value="Equity Research">Equity Research 📈</MenuItem>
                        <MenuItem value="Digital Marketing">Digital Marketing 🌐</MenuItem>
                    </Select>
                </FormControl>
                <FormControl fullWidth>
                    <InputLabel id="mentor-select-label">Select a Mentor 🧑‍🏫</InputLabel>
                    <Select
                        labelId="mentor-select-label"
                        value={selectedMentor}
                        onChange={e => setSelectedMentor(e.target.value)}
                        label="Select a Mentor"
                    >
                        {filteredMentors.map(mentor => (
                            <MenuItem key={mentor.id} value={mentor.id}>
                                {mentor.name}
                            </MenuItem>
                        ))}
                    </Select>
                </FormControl>
                <FormControl fullWidth>
                    <InputLabel id="duration-select-label">Duration ⏳</InputLabel>
                    <Select
                        labelId="duration-select-label"
                        value={duration}
                        onChange={e => setDuration(e.target.value)}
                        label="Duration"
                    >
                        <MenuItem value={30}>30 mins 🕒</MenuItem>
                        <MenuItem value={45}>45 mins ⏲️</MenuItem>
                        <MenuItem value={60}>60 mins ⏰</MenuItem>
                    </Select>
                </FormControl>
                <TextField
                    label="Session Time"
                    type="datetime-local"
                    fullWidth
                    InputLabelProps={{ shrink: true }}
                    value={sessionTime}
                    onChange={e => setSessionTime(e.target.value)}
                />
                <Typography variant="h6" component="p" gutterBottom>
                    Price: ₹{price} 💵
                </Typography>
                <Button 
                    variant="contained" 
                    color="primary" 
                    fullWidth 
                    onClick={handleSchedule}
                >
                    Schedule Session 📅
                </Button>
            </Box>
        </Container>
    );
};

export default Scheduler;
