import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { Box, Button, TextField, Typography, Select, MenuItem } from '@mui/material';
import { styled } from '@mui/system';
import './Signup.css';

const GlassContainer = styled(Box)({
    backdropFilter: 'blur(10px)',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    borderRadius: '10px',
    boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
    border: '1px solid rgba(255, 255, 255, 0.3)',
    padding: '2rem',
    maxWidth: '400px',
    margin: 'auto',
    marginTop: '5rem',
});

const Signup = () => {
    const [name, setName] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [role, setRole] = useState('student');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignup = () => {
        const signupData = { 
            name, 
            company_name: role === 'mentor' ? companyName : null, 
            role, 
            password 
        };

        axios.post('http://localhost:5000/api/auth/signup', signupData)
            .then(response => {
                alert('Signup successful!');
                navigate('/login');
            })
            .catch(err => {
                console.error(err);
                alert('Error during signup. Please try again.');
            });
    };

    return (
        <GlassContainer>
            <Typography variant="h4" align="center" gutterBottom>
                Signup
            </Typography>
            <TextField
                label="Name"
                value={name}
                onChange={e => setName(e.target.value)}
                fullWidth
                margin="normal"
                variant="outlined"
            />
            {role === 'mentor' && (
                <TextField
                    label="Company Name"
                    value={companyName}
                    onChange={e => setCompanyName(e.target.value)}
                    fullWidth
                    margin="normal"
                    variant="outlined"
                />
            )}
            <Select
                value={role}
                onChange={e => setRole(e.target.value)}
                fullWidth
                margin="normal"
                variant="outlined"
            >
                <MenuItem value="student">Student</MenuItem>
                <MenuItem value="mentor">Mentor</MenuItem>
            </Select>
            <TextField
                label="Password"
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                fullWidth
                margin="normal"
                variant="outlined"
            />
            <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={handleSignup}
                sx={{ marginTop: '1rem' }}
            >
                Signup
            </Button>
            <Typography variant="body2" align="center" sx={{ marginTop: '1rem' }}>
                Already have an account? <Link to="/login">Login</Link>
            </Typography>
        </GlassContainer>
    );
};

export default Signup;
