// src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Container, TextField, Button, Typography, Select, MenuItem, InputLabel, FormControl, Box } from '@mui/material';

const Login = () => {
    const [name, setName] = useState('');
    const [role, setRole] = useState('student');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = () => {
        const loginData = { name, role, password };

        axios.post('http://localhost:5000/api/auth/login', loginData)
            .then(response => {
                alert('Login successful! üéâ');
                navigate('/scheduler');
            })
            .catch(err => {
                console.error(err);
                alert('Error during login. Please try again. ‚ùå');
            });
    };

    return (
        <Container maxWidth="sm">
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: 2,
                    mt: 5,
                    p: 7,
                    boxShadow: 3,
                    borderRadius: 6,
                    background: '#4454835a',
                    backdropFilter: 'blur(10px)'
                }}
            >
                <Typography variant="h4" component="h1" gutterBottom>
                    Login üîë
                </Typography>
                <TextField
                    label="Name"
                    variant="outlined"
                    fullWidth
                    value={name}
                    onChange={e => setName(e.target.value)}
                />
                <FormControl fullWidth>
                    <InputLabel id="role-select-label">Role</InputLabel>
                    <Select
                        labelId="role-select-label"
                        value={role}
                        onChange={e => setRole(e.target.value)}
                        label="Role"
                    >
                        <MenuItem value="student">Student üéì</MenuItem>
                        <MenuItem value="mentor">Mentor üßë‚Äçüè´</MenuItem>
                    </Select>
                </FormControl>
                <TextField
                    label="Password"
                    type="password"
                    variant="outlined"
                    fullWidth
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                />
                <Button 
                    variant="contained" 
                    color="primary" 
                    fullWidth 
                    onClick={handleLogin}
                >
                    Login üöÄ
                </Button>
            </Box>
        </Container>
    );
};

export default Login;
